import React, { Component } from 'react';
import "./table.css"

class Table extends Component {
    constructor(props) {
       super(props)
       this.state = {
          students: [

          ]
       }
    }
 
    renderTableHeader() {
       let header = this.props.columns
       return header.map((key, index) => {
          return <th key={index}>{key.toUpperCase()}</th>
       })
    }
 
    renderTableData() {
       return this.props.noteData.map((book, index) => {
          const { id, title, authors, contentLen } = book //destructuring
          return (
             <tr key={id}>
                <td>{id}</td>
                <td>{title}</td>
                <td>{authors}</td>
                <td>{contentLen}</td>
             </tr>
          )
       })
    }
 
    render() {
       return (
          <div>
             <h1 id='title'>React Dynamic Table</h1>
             <table id='students'>
                <tbody>
                   <tr>{this.renderTableHeader()}</tr>
                   {this.renderTableData()}
                </tbody>
             </table>
          </div>
       )
    }
 }
 
 export default Table;
